static void my_handler(const lcm_recv_buf_t *rbuf, const char * channel, const RXState_RXState_t * msg, void * user){ 
    printf("Received Message...\n"); 
    printf("USV Pose:\n"); 
    printf("Pose1:%f Pose2:%f Pose3:%f\n",msg->USV_pose[0],msg->USV_pose[1],msg->USV_pose[2]); 
    printf("USV Vel:\n"); 
    printf("Vel1:%f Vel2:%f\n",msg->USV_vel[0],msg->USV_vel[1]); 
    printf("USV Euler:\n"); 
    printf("Euler1:%f Euler2:%f Euler3:%f\n", msg->USV_euler[0],msg->USV_euler[1],msg->USV_euler[2]);  
}

