/* Function: mdlOutputs =======================================================
 *
*/
static void mdlOutputs(SimStruct *S, int_T tid)
{
    lcm_t *lcm = lcm_create("udpm://239.255.76.67:7667?ttl=25"); 
    if(!lcm)
        exit(1); 
    RXState_RXState_t_subscribe(lcm, "EXAMPLE", &my_handler, NULL); 
    while(1)
        lcm_handle(lcm); 
    lcm_destroy(lcm); 
}




